
declare interface StringMap<T> {
    [k: string]: T;
}

declare interface NumberMap<T> {
    [k: number]: T;
}


declare interface NumberDict<T> {
    [k: number]: T | undefined;
}

type TErrorOrNot = Error | null | undefined;

type AsnycCallback = (e: TErrorOrNot, d?: any, ...other: [unknown?])=>void;
type AsyncFunction = (success: (d?: any)=>void, reject?: (e: Error)=>any)=>void;
type DataHandler = (d: any) => any;
type ErrorHandler = (e: Error) => void;