const assert = require('assert');

const Facility = require('CommonUtils').Facility;

console.log(Facility.LocalDate());

let generator = (function* () {})();
let genFunction = function* () {
    yield 42;
};

assert.deepEqual(Facility.IsGeneratorFunction(null), false);
assert.deepEqual(Facility.IsGeneratorFunction(undefined), false);
assert.deepEqual(Facility.IsGeneratorFunction(42), false);
assert.deepEqual(Facility.IsGeneratorFunction('foo'), false);
assert.deepEqual(Facility.IsGeneratorFunction(function fn() {}), false);
assert.deepEqual(Facility.IsGeneratorFunction(generator), false);
assert.deepEqual(Facility.IsGeneratorFunction(genFunction), true);