const Hash = require('../lib/Hash.js');
const perf = require('Performance');

perf("hash", () => {
    return Hash.StrongHash('abcdefghijklmnopqrstuvwxyz1234567890');
});

perf("batch- hash", () => {
    for (let i = 0; i < 10000; i++) {
        Hash.StrongHash('abcdefghijklmnopqrstuvwxyz1234567890');
    }
});