import { randomBytes as rb } from 'crypto';

var GUID = require('../index.js').GUID;

module.exports = function () {
    return rb(16);
};

const TEST_COUNT = 100;
let Data = new Array(TEST_COUNT);

function Test32() {
    for (let i = 0; i < TEST_COUNT; i++) {
        Data[i] = GUID.New32();
    }
}

function Test64() {
    for (let i = 0; i < TEST_COUNT; i++) {
        Data[i] = GUID.New64();
    }
}

function Gen() {
    for (let i = 0; i < TEST_COUNT; i++) {
        Data[i] = GUID.Gen();
    }
}

function TestRaw() {
    for (let i = 0; i < TEST_COUNT; i++) {
        Data[i] = GUID.Raw32();
    }
}

function Output() {
    for (let i = 0; i < TEST_COUNT; i++) {
        console.log(Data[i]);
    }
}


function Test() {
    Test32();
    Output();

    Test64();
    Output();

    Gen();
    Output();
}

Test();

console.log(GUID.MAAC);
console.log("end");