const Client = require("../lib/HttpClient.js");

let client = new Client();


let url = "https://cloudinary-a.akamaihd.net/vivivi/image/upload/v1515573440/stl0547622_01.jpg";
let file = process.env.Temp + "/js.dowonload.js.jpg";

async function Test() {
    console.log(file);
    await client.Download(url, file);
    console.log('Done');

    let d = await client.Get("http://www.baidu.com");
    console.log('Got:', d);
}

Test().then(
    () => {
        console.log("OK");
    }
);