import DirectLogger from './lib/Logger/DirectLogger';
import { ILoggerAgent } from './lib/LoggerManager';

export { ILogger } from './lib/LoggerManager';

let LoggerAgent: ILoggerAgent = DirectLogger;


export const SysLogger = {
    Get(catelog?: string) {
        return LoggerAgent.Get(catelog);
    },

    Append(name: string[] | any) {
        LoggerAgent.Append(name);
    },

    set Async(flag: boolean) {
        LoggerAgent = flag ? require('./Logger/ProcessLogger') : DirectLogger;
    },

    StartUDPServer() {
        require('./Logger/UDPLogger.js');
    }
};

export const Default = SysLogger.Get();
