import { Server, MapRouter, StaticHandler, RouterHandler } from 'WebService';
import { Facility } from 'CommonUtils';
import { SysLogger } from 'Logger';

SysLogger.Async = false;
SysLogger.Append(['Console']);
Facility.SafeException(SysLogger.Get());

let RestfulRouter = new MapRouter({ "test": require('./DAO/TestBean.js') });

let BaseRouter = new MapRouter({
    'res': new StaticHandler(RestfulRouter),

});

const http = new Server(new RouterHandler(BaseRouter), {});

http.Start();
