export interface Txn {
    getString(db: Dbi, key: Buffer): string;
    putString(db: Dbi, key: Buffer, value: string): void;
    del(db: Dbi, key: Buffer): void;

    commit(): void;
    abort(): void;
}

export type EnvCnf = {
    path: string;
    mapSize: number;  
    maxDbs: number;
}

export type DbiCnf = {
    name: string;
    create: boolean;
    keyIsBuffer?: boolean;
    keyIsString?: boolean;
}

export interface Dbi {
    close(): void;
}

export class Env {
    beginTxn(): Txn;
    openDbi(opts: DbiCnf): Dbi;

    open(opts: EnvCnf): void;
    close(): void;
}
